INSERT INTO pins (pin_name, pin_description, image_link, latitude, longitude, pin_category_id) VALUES
('Az 1648. évi hősnők és 1848 emlékoszlopa', 'Ez a város első nem vallási témájú köztéri alkotása, amely Gyarmat várának 1648-as sikeres megvédésében szerepet játszó nők és az 1848-as forradalom előtt állít emléket. Költségét adományokból gyűjtötték, és obeliszket állítottak. Ünnepélyes felavatására 1908-ban került sor.', NULL, 48.075361, 19.289000, (SELECT id FROM pin_categories WHERE category_name = 'Történelmi események')),
('I. világháborús emlékmű', 'Ezt az emlékművet az első világháborúban elhunyt polgárok emlékére állították. A költségét adományokból gyűjtötték össze. Az avatásra 1929. október 20-án került sor a vármegyeháza melletti téren, Horthy Miklós kormányzó jelenlétében. Mivel a terület ideiglenes volt, a szobrot 1937-ben áthelyezték a Palóc ligetbe. Az emlékművet később felújították, többek között 2016 februárjában, során archív fotók és a makett alapján pótolták az oroszlán hiányzó részeit.', NULL, 48.074611, 19.290111, (SELECT id FROM pin_categories WHERE category_name = 'Történelmi események')),
('16-os honvéd szobra', 'Az első világháborús honvéd emlékmű felállítását 1935-ben kezdeményezték a 16. honvéd gyalogezred volt tagjai, és adománygyűjtéssel valósult meg. Körmendi-Frim Jenő készítette. Az emlékművet 1937. október 27-én avatták fel a Hősök terén. A szobrot 1967-ben áthelyezték a Palóc liget egyik félreeső sarkába, majd 1990-ben került vissza az eredeti helyére. 2015-ben felújították.', NULL, 48.072389, 19.290472, (SELECT id FROM pin_categories WHERE category_name = 'Történelmi események')),
('Madách-szobor', 'Madách Imre, a hazai irodalom kimagasló egyéniségének szobrát Sidló Ferenc készítette. A vármegye zárt körű pályázatot írt ki 1935-ben. A szobor felállításának helye a Törvényszék és a Vármegyeháza közötti tér volt. Az ünnepélyes átadásra 1937. szeptember 26-án került sor.', NULL, 48.076611, 19.289417, (SELECT id FROM pin_categories WHERE category_name = 'Neves személyek')),
('Mikszáth-mellszobor', 'Mikszáth Kálmán mellszobra, amelyet Herczeg Klára Munkácsy-díjas szobrászművész készített. A városi tanács döntött a felállításról az 1950-es évek végén. Az elkészült mellszobrot 1961-ben avatták fel a vármegyeháza előtt, a Köztársaság téren. A mellszobrot 2012-2013-ban felújították.', NULL, 48.077028, 19.290722, (SELECT id FROM pin_categories WHERE category_name = 'Neves személyek')),
('Petőfi-szobor', 'Petőfi Sándorról készült egész alakos bronzszobor, amelyet Borbás Tibor készített. 1977. március 15-én adták át a Palóc ligetben. A szobornál tartják a március 15-i nemzeti ünnep városi megemlékezését. A kiterjesztett karok által kifeszített köpeny alakja miatt a helyiek gyakran gúnyosan „denevérnek” szokták hívni.', NULL, 48.074917, 19.290889, (SELECT id FROM pin_categories WHERE category_name = 'Neves személyek')),
('Kondor Ernő-mellszobor', 'Búza Barna Kondor Ernőről készített pirogránit mellszobra 1980 óta áll a Helytörténeti Gyűjtemény udvari részén.', NULL, 48.079972, 19.304139, (SELECT id FROM pin_categories WHERE category_name = 'Neves személyek')),
('Komjáthy Jenő-mellszobor', 'Győrfi Sándor Komjáthy Jenőről készült mellszobrát 1981-ben adták át a Palóc ligetben.', NULL, 48.074583, 19.288861, (SELECT id FROM pin_categories WHERE category_name = 'Neves személyek')),
('Szabó Lőrinc-mellszobor (2017)', 'Az új Szabó Lőrinc-mellszobor, amelyet Petró György balassagyarmati szobrászművész készített , valamennyire idézi meg az 1977-ben felállított, 1997-ben eltűnt Borsos Miklós-féle alkotást. Az avató ünnepségre 2017. október 3-án került sor.', NULL, 48.074972, 19.289000, (SELECT id FROM pin_categories WHERE category_name = 'Neves személyek')),
('A csehek kiverésének emléktáblája', 'Balassagyarmat első közpénzből elkészített köztéri alkotása, amely az 1919. január 29-i felkelésnek állít emléket. Az emléktáblát Keviczky Hugó készítette. 1922. november 22-én avatták fel a városháza falán , Horthy Miklós is beszédet mondott. Később eltávolították a bronz turulmadarat és megfordították a táblát, új szöveget vésve rá. A rendszerváltás után visszafordították és a turult másolattal pótolták.', NULL, 48.077389, 19.291083, (SELECT id FROM pin_categories WHERE category_name = 'Emléktáblák')),
('Madách Imre emléktáblája', 'Keviczky Hugó által készített emléktábla. 1925. szeptember 6-án avatták fel a vármegyeháza falán a gazdasági kiállítás megnyitója alatt. Az avatáson ünnepi beszédet mondott Scitovszky Béla házelnök, Horthy Miklós kormányzó és Berzeviczy Albert MTA-elnök is. A tábla feliratát később részben eltüntették , majd civil kezdeményezésre újra olvashatóvá tették vagy újravésték.', NULL, 48.076917, 19.290528, (SELECT id FROM pin_categories WHERE category_name = 'Emléktáblák')),
('Mikszáth Kálmán emléktáblája', 'Keviczky Hugó által készített emléktábla. 1925. szeptember 6-án avatták fel a vármegyeháza falán a gazdasági kiállítás megnyitója alatt. Az avatáson ünnepi beszédet mondott Scitovszky Béla házelnök, Horthy Miklós kormányzó és Berzeviczy Albert MTA-elnök is. A tábla feliratát később részben eltüntették , majd 1990 után a ''csonka'' szavakat újra olvashatóvá tették.', NULL, 48.076833, 19.290361, (SELECT id FROM pin_categories WHERE category_name = 'Emléktáblák')),
('Balassi Bálint Gimnázium hősi halottjainak emléktáblája', 'Az első világháborúban elhunyt tanárok és diákok emléktáblája. Az emléktáblát Branceisz János készítette. 1926. június 10-én leplezték le a Magyar Királyi Állami Reálgimnázium fennállásának 25. évfordulójának ünnepségén.', NULL, 48.075722, 19.294167, (SELECT id FROM pin_categories WHERE category_name = 'Emléktáblák')),
('A törvényszék szobrai', 'A törvény őrét jelképező praetor és Iustitia igazságszolgáltatás római istennőjét ábrázoló szobrok a Balassagyarmati Törvényszék főhomlokzatán. Az épületet 1912-ben adták át. A szobrok állapotának leromlása miatt 2022-2023-ban új szobor másolatokkal cserélték az eredetieket.', NULL, 48.076389, 19.288889, (SELECT id FROM pin_categories WHERE category_name = 'Egyéb alkotások')),
('Lovagló fiú szobor', 'Vasas Károly által készített bronzszobor. 1968-ban került az Ifjúság Úti Általános Iskola elé , majd az iskola bezárásával a Szabó Lőrinc Általános Iskola elé helyezték át.', NULL, 48.067806, 19.304778, (SELECT id FROM pin_categories WHERE category_name = 'Egyéb alkotások')),
('Balassi Bálint-szobor', 'Párkányi Raab Péter készítette derékig megmintázott szobor, amely Balassit ábrázolja írás közben. 2004-ben avatták fel a Balassi Bálint Gimnázium kertjében , a Balassi-sztélé korábbi helyén. (Megjegyzés: Nem köztéri alkotás.)', NULL, 48.075833, 19.294167, (SELECT id FROM pin_categories WHERE category_name = 'Nem köztéri alkotások')),
('Dísztálak', 'Szabady Veronika keramikus által készített hat darab, pirogránitból készült sorozat, három groteszk arcot ábrázol. 1974-ben állították ki , majd a főtéri épület homlokzatán kihelyezték. Az épület felújítását követően nem kerültek vissza az eredeti helyükre , hanem a Jánossy Képtárba kerültek restaurálás után. (Megjegyzés: Lebontott/Áthelyezett alkotás.)', NULL, 48.076750, 19.291139, (SELECT id FROM pin_categories WHERE category_name = 'Lebontott alkotások')),
('Szabó Lőrinc-mellszobor (Borsos Miklós, ellopott)', 'Borsos Miklós készítette mellszobor. 1977. március 30-án avatták fel a Palóc ligetben. A mellszobrot 1997 áprilisában ellopták. (Megjegyzés: Lebontott/Ellopott alkotás. Helyére később új szobor került.)', NULL, 48.074972, 19.289000, (SELECT id FROM pin_categories WHERE category_name = 'Lebontott alkotások'));